import React from 'react'
import { connect } from 'react-redux'
import { Route } from 'react-router-dom'
import { createStructuredSelector } from 'reselect'

import { fetchCollectionsStartAsync } from '../../redux/shop/shop.actions'
import {  selectIsCollectionsLoaded, selectIsFetchingCollections } from '../../redux/shop/shop.selectors'


import CollectionOverview from '../../components/collections-overview/collections-overview.component'
import CollectionPage from '../collection/collection.component'
import WithSpinner from '../../components/with-spinner/with-spinner.component'
import { CollectionsOverviewContainer } from '../../components/collections-overview/collections-overview.styles'





const CollectionPageWithSpinner = WithSpinner(CollectionPage)


class  ShopPage extends React.Component {



    componentDidMount() {

        const { fetchCollectionsStartAsync } = this.props

        fetchCollectionsStartAsync()

    }

       render() {

          const { match,  isCollectionsLoaded } = this.props


          return  ( <div className = 'shop-page' >

              <Route exact path={`${match.path}`}  component = { CollectionsOverviewContainer}  /> } />
              <Route path={`${match.path}/:collectionId`} render={(props) => (<CollectionPageWithSpinner isLoading = { !isCollectionsLoaded } {...props } /> )} />

           </div> )


       }
    }


const mapStateToProps = createStructuredSelector ({

    isFetchingCollections: selectIsFetchingCollections,
    isCollectionsLoaded: selectIsCollectionsLoaded

})
const mapDispatchToProps = dispatch => ({

    fetchCollectionsStartAsync: () => dispatch(fetchCollectionsStartAsync())
})


export default connect(mapStateToProps, mapDispatchToProps)(ShopPage)
