import { takeEvery} from 'redux-saga/effects'

import   ShopActionTypes  from './shop.types'


import { firestore, convertCollectionsSnapshotToMap } from '../../firebase/firebase.utils'



export function*  fetchCollectionsStartAsync() {

    yield console.log('YOPII Fired!!!!')


    const collectionRef = firestore.collection('collection')

    dispatch(fetchCollectionsStart())

    collectionRef.get().then(async shot => {

        const collectionsMap = convertCollectionsSnapshotToMap(shot)

        dispatch(fetchCollectionsSuccess(collectionsMap))

    }).catch(error => dispatch(fetchCollectionsFailure(error.errorMessage)))
}


export function* fetchCollectionsStart(){

  yield  takeEvery( ShopActionTypes.FETCH_COLLECTIONS_START,  fetchCollectionsStartAsync)
}
