import { takeLatest, put, call, all } from 'redux-saga/effects'


import UserActionTypes from './user.types'

import { signInSuccess, signInFailure, signOutSuccess, signOutFailure, signUpSucces, signUpFailure } from './user.actions'

import { auth, googleProvider, createUserProfileDocument, getCurrentUser } from './../../firebase/firebase.utils'


export function* getSnapshotFromUserAuth( userAuth, additionalData) {

    const userRef = yield call(createUserProfileDocument, userAuth, additionalData)

    const snapShot = yield userRef.get()

    yield put(

        signInSuccess({ id: snapShot.id, ...snapShot.data() })

    )

}
export function* signInWithGoogle() {

    try {

           const { user } = yield auth.signInWithPopup(googleProvider)


            yield getSnapshotFromUserAuth(user)



    } catch (error) {

        yield put( signInFailure(error))
    }
}


export function* signInWithEmail({ payload: { email, password } }) {

    try {

        const { user } = yield auth.signInWithEmailAndPassword(email, password)

        yield getSnapshotFromUserAuth(user)



    } catch (error) {

        yield put(
                signInFailure(error)
             )
    }
}

export function* isUserAuthenticated() {
    try {

        const userAuth = yield getCurrentUser()

        if (!userAuth) return

        yield getSnapshotFromUserAuth(userAuth)

    } catch (error) {

        yield put(

            signInFailure(error)
        )
    }

}

export function* signOut() {

    try {

        yield auth.signOut()

        yield put (

            signOutSuccess()
        )

    } catch (error) {

        put(

            signOutFailure(error)
        )
    }
}

export function* signUp({payload : { email, password, displayName}}) {

   try {

       const { user } = yield auth.createUserWithEmailAndPassword(email, password)

       yield put(

           signUpSucces({user,  additionalData: displayName })
       )

   } catch (error) {

    yield put(

        signUpFailure(error)
    )
   }
}

export function* signInAfterSignUp({payload: {user, additionalData}}) {


}
export function* onGoogleSignInStart(){

       yield takeLatest(UserActionTypes.GOOGLE_SIGN_IN_START, signInWithGoogle)
}

export function* onEmailSignInStart() {

    yield takeLatest(UserActionTypes.EMAIL_SIGN_IN_START, signInWithEmail)
}

export function* onCheckUserSession()  {

    yield  takeLatest(UserActionTypes.CHECK_USER_SESSION,isUserAuthenticated)
}

export function* OnSignOutStart() {

    yield takeLatest(UserActionTypes.SIGN_OUT_START, signOut)
}

export function* onSignUpStart(){

    takeLatest(UserActionTypes.SIGN_OUT_START, signUp)
}


export function* onSignUpSuccess() {

    takeLatest(UserActionTypes.SIGN_UP_SUCCESS, signInAfterSignUp)
}
export  function* userSagas() {

    yield all([ call(onGoogleSignInStart), 
                call(onEmailSignInStart), 
                call(onCheckUserSession),
                call(OnSignOutStart),
            ])

}
